mapper.insert:
INSERT INTO feed
   SET writer_user_id = #{writerUserId}
     , contents = #{contents}
     , location = #{location}

mapper.select:
SELECT a.feed_id AS feedId, a.contents, a.location, a.created_at AS createdAt , b.user_id AS writerUserId , if(b.nick_name IS NULL, b.uid, b.nick_name) as writerNm , b.pic as writerPic , IF(c.feed_id IS NULL, 0, 1) as isLike FROM feed a JOIN user b ON a.writer_user_id = b.user_id LEFT JOIN feed_like c ON a.feed_id = c.feed_id AND c.user_id = #{signedUserId}
WHERE a.writer_user_id = #{profileUserId}
ORDER BY a.feed_id desc LIMIT #{startIdx}, #{size}

SELECT T1.*, T2.pic FROM ( SELECT A.feed_id AS feedId, A.contents, A.location , A.created_at AS createdAt , B.user_id AS writerUserId , IF(B.nick_name IS NULL, B.uid, B.nick_name) AS writerNm , B.pic AS writerPic , IF(C.feed_id IS NULL, 0, 1) AS isLike FROM feed A INNER JOIN user B ON A.writer_user_id = B.user_id LEFT JOIN feed_like C ON A.feed_id = C.feed_id AND C.user_id = #{signedUserId}
WHERE A.writer_user_id = #{profileUserId}
ORDER BY A.feed_id DESC LIMIT #{startIdx}, #{size} ) T1 INNER JOIN feed_pic T2 ON T1.feedId = T2.feed_id

SELECT T1.*, T2.pic, T3.* FROM ( SELECT A.feed_id AS feedId, A.contents, A.location , A.created_at AS createdAt , B.user_id AS writerUserId , IF(B.nick_name IS NULL, B.uid, B.nick_name) AS writerNm , B.pic AS writerPic , IF(C.feed_id IS NULL, 0, 1) AS isLike FROM feed A INNER JOIN user B ON A.writer_user_id = B.user_id LEFT JOIN feed_like C ON A.feed_id = C.feed_id AND C.user_id = #{signedUserId}
WHERE A.writer_user_id = #{profileUserId}
ORDER BY A.feed_id DESC LIMIT #{startIdx}, #{size} ) T1 INNER JOIN feed_pic T2 ON T1.feedId = T2.feed_id LEFT JOIN ( SELECT A.feed_id, A.feed_comment_id, A.`COMMENT` , C.user_id AS commentWriterUserId , IF(C.nick_name IS NULL, C.uid, C.nick_name) AS commentWriterNm , C.pic AS commentWriterPic FROM feed_comment A LEFT JOIN feed_comment B ON A.feed_id = B.feed_id AND A.feed_comment_id > B.feed_comment_id INNER JOIN user C ON A.user_id = C.user_id GROUP BY A.feed_id, A.feed_comment_id, A.`COMMENT` HAVING COUNT(A.feed_id) < 4 ) T3 ON T1.feedId = T3.feed_id

mapper.delete:
DELETE FROM feed
WHERE feed_id = #{feedId}
AND writer_user_id = #{signedUserId}
